INSERT INTO `sanpham` (`masanpham`, `tensanpham`, `gia`, `iddm`, `mota`, `motachitiet`) VALUES
(55, 'Cây lộc vừng', 799000, 23, 'Cây Lộc Vừng là một trong những cây hay được lựa chọn làm cây bóng mát, cây trồng trước cửa nhà, trong sân.', '<h2><strong>ĐẶC ĐIỂM C&Acirc;Y LỘC VỪNG</strong></h2>\r\n\r\n<p><strong>T&ecirc;n thường gọi:</strong>&nbsp;<a href=\"https://webcaycanh.com/cay-loc-vung/\">C&acirc;y Lộc Vừng</a></p>\r\n\r\n<p><strong>T&ecirc;n khoa học:</strong>&nbsp;Barringtonia acutangula</p>\r\n\r\n<p><strong>Bộ (ordo):</strong>&nbsp;Ericales</p>\r\n\r\n<p><strong>Họ (familia):</strong>&nbsp;Lecythidaceae</p>\r\n\r\n<p><strong>Chi (genus):</strong>&nbsp;Barringtonia</p>\r\n\r\n<p><strong>Lo&agrave;i (species):</strong>&nbsp;B. acutangula</p>\r\n\r\n<p>C&acirc;y thuộc d&ograve;ng c&acirc;y bản địa của c&aacute;c v&ugrave;ng đất ẩm ven biển Nam &Aacute; v&agrave; Bắc &Uacute;c, từ Afghanistan đến Philippin v&agrave; Queensland. Ở Việt Nam c&acirc;y hiện nay cũng được trồng ở khắp mọi nơi từ Bắc v&agrave;o Nam v&agrave; ra tận C&ocirc;n Đảo.</p>\r\n\r\n<p><img alt=\"cây lộc vừng công trình đẹp\" src=\"https://webcaycanh.com/wp-content/uploads/2023/07/cay-loc-vung-cong-trinh-dep-400x300.jpg\" style=\"height:300px; width:400px\" /></p>\r\n\r\n<h2><strong>&Yacute; NGHĨA PHONG THỦY CỦA C&Acirc;Y LỘC VỪNG</strong></h2>\r\n\r\n<p>C&acirc;y được xếp v&agrave;o loại c&acirc;y cảnh qu&yacute; trong bộ: Sanh &ndash; Sung &ndash; T&ugrave;ng &ndash; Lộc. C&aacute;i t&ecirc;n một phần n&agrave;o đ&oacute; cũng to&aacute;t l&ecirc;n được &yacute; nghĩa phong thủy của c&acirc;y.</p>\r\n\r\n<p>Lộc trong chữ &ldquo;T&agrave;i Lộc&rdquo;, Vừng trong chữ &ldquo;Vừng ơi mở ra&rdquo;. C&oacute; nghĩa l&agrave; mở cửa ra rước lộc về nh&agrave;.</p>\r\n\r\n<p>Ngo&agrave;i ra theo quan niệm ng&agrave;y xưa th&igrave; m&agrave;u đỏ v&agrave; những b&aacute;nh ph&aacute;o đại diện cho nh&agrave; c&oacute; tin hỷ. Th&igrave; hoa của c&acirc;y d&agrave;i v&agrave; đỏ cũng được quan niệm mang đến hỷ sự cho nh&agrave;.</p>\r\n'),
(56, 'Cây phát tài', 430000, 23, 'Cây Phát Tài 5 thân có chiều cao 1m6 tùy vào độ phát triển lá mà có thể cao hơn nữa, phù hợp để các góc lớn của văn phòng, phòng khách rộng. Cây thường được tặng làm quà, tân gia, khai trương cửa hàng...', '<p>C&acirc;y Ph&aacute;t T&agrave;i 5 th&acirc;n cao 1m6 c&oacute; l&aacute; m&agrave;u xanh x&ograve;e rộng đi k&egrave;m với c&aacute;c g&acirc;n m&agrave;u v&agrave;ng. L&aacute; xanh tốt quanh năm. C&acirc;y thường ra hoa v&agrave;o gần tết. Chậu c&acirc;y gồm 5 th&acirc;n trong phong thủy c&oacute; &yacute; nghĩa l&agrave; sức khỏe, c&acirc;y mang &yacute; nghĩa ph&aacute;t t&agrave;i. Khi gia chủ sở hữu c&acirc;y sẽ vừa c&oacute; sức khỏe v&agrave; tiền t&agrave;i. Ph&ugrave; hợp l&agrave;m qu&agrave; tặng khai trương, t&acirc;n gia, c&acirc;y để ph&ograve;ng kh&aacute;ch, ph&ograve;ng gi&aacute;m đốc, sảnh kh&aacute;ch sạn, quầy lễ t&acirc;n lớn.</p>\r\n\r\n<p><img alt=\"cây phát tài 5 thân cao\" src=\"https://webcaycanh.com/wp-content/uploads/2022/05/cay-phat-tai-5-than-cao-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<h2>ĐẶC ĐIỂM CỦA C&Acirc;Y PH&Aacute;T T&Agrave;I 5 TH&Acirc;N CAO 1M6</h2>\r\n\r\n<p><strong>T&ecirc;n thường gọi</strong>:&nbsp;<a href=\"https://webcaycanh.com/cay-phat-tai-5-than-cao-1m6/\">C&acirc;y Ph&aacute;t T&agrave;i cao 1m6</a>, c&acirc;y Thiết Mộc Lan gh&eacute;p, c&acirc;y Ph&aacute;t T&agrave;i kh&uacute;c</p>\r\n\r\n<p><strong>T&ecirc;n khoa học</strong>: Dracaena fragrans</p>\r\n\r\n<p><strong>Giới (regnum)</strong>: Plantae</p>\r\n\r\n<p><strong>Bộ (ordo)</strong>: Asparagales</p>\r\n\r\n<p><strong>Họ (familia)</strong>: Ruscaceae</p>\r\n\r\n<p><strong>Chi (genus)</strong>: Dracaena</p>\r\n\r\n<p><strong>Lo&agrave;i (species)</strong>: D. fragrans</p>\r\n\r\n<p><strong>Nguồn gốc v&agrave; xuất xứ</strong>: T&acirc;y Phi, Tanzania v&agrave; Zambia</p>\r\n\r\n<p>C&acirc;y được gi&acirc;m từ c&aacute;c c&agrave;nh c&oacute; k&iacute;ch cỡ kh&aacute;c nhau để tạo được t&aacute;n c&acirc;n đối v&agrave; h&agrave;i h&ograve;a giữa cao v&agrave; thấp.</p>\r\n\r\n<p>C&acirc;y Ph&aacute;t T&agrave;i nếu được đem ra ngo&agrave;i m&ocirc;i trường v&agrave; trồng v&agrave;o đất để ph&aacute;t triển thoải m&aacute;i c&oacute; thể cao tới&nbsp; 6m. C&acirc;y thuộc loại th&acirc;n gỗ.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2018/12/cay-phat-tai-ra-hoa-la-diem-gi.jpg\"><img alt=\"cây phát tài ra hoa là điềm gì\" src=\"https://webcaycanh.com/wp-content/uploads/2018/12/cay-phat-tai-ra-hoa-la-diem-gi-300x400.jpg\" style=\"height:400px; width:300px\" /></a></p>\r\n\r\n<p>C&acirc;y c&oacute; l&aacute; mọc xung quanh th&acirc;n c&acirc;y th&agrave;nh h&igrave;nh nơ, l&aacute; d&agrave;i gần giống l&aacute; c&acirc;y ng&ocirc; nhưng ngắn hơn, c&oacute; m&agrave;u xanh sẫm v&agrave; b&oacute;ng, tr&ecirc;n l&aacute; c&oacute; một hoặc hai dải m&agrave;u dọc từ cuống tới ngọn l&aacute;, m&agrave;u v&agrave;ng nhạt v&agrave; đậm dần v&agrave;o giữa.&nbsp;<strong>C&acirc;y ph&aacute;t t&agrave;i</strong>&nbsp;c&oacute; thể ra hoa, hoa m&agrave;u trắng v&agrave; rất thơm.</p>\r\n\r\n<h2>&Yacute; NGHĨA PHONG THỦY C&Acirc;Y PH&Aacute;T T&Agrave;I</h2>\r\n\r\n<p><strong>&Yacute; nghĩa</strong>: Trong phong thủy c&acirc;y ph&aacute;t t&agrave;i l&agrave; một trong những loại đứng h&agrave;ng đầu mang về t&agrave;i lộc v&agrave; may mắn cho chủ sở hữu.</p>\r\n\r\n<p><img alt=\"thiết mộc lan đẹp\" src=\"https://webcaycanh.com/wp-content/uploads/2022/05/thiet-moc-lan-dep-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<p>2 c&acirc;y tượng trưng cho t&igrave;nh y&ecirc;u</p>\r\n\r\n<p>3 hoặc 6 c&acirc;y tượng trưng cho hạnh ph&uacute;c</p>\r\n\r\n<p>5 hoặc 7 c&acirc;y tượng trưng cho sức khỏe</p>\r\n\r\n<p>8 c&acirc;y tượng trưng cho lộc</p>\r\n\r\n<p>9 c&acirc;y tượng trưng cho sự may mắn n&oacute;i chung</p>\r\n\r\n<p>21 c&acirc;y tượng trưng cho lời cầu ph&uacute;c</p>\r\n'),
(57, 'Cây phát tài núi', 949000, 23, 'Cây Phát Tài Núi hay còn gọi là cây Đại Lộc đang là một trong những cây nội thất rất được ưa chuộng. Cây có chiều cao 1m6 - 1m7 phù hợp làm quà tặng khai trương, tân gia, cây để văn phòng, trong nhà.', '<h2><strong>ĐẶC ĐIỂM CỦA C&Acirc;Y PH&Aacute;T T&Agrave;I N&Uacute;I 3 TH&Acirc;N</strong></h2>\r\n\r\n<p>T&ecirc;n thường gọi: C&acirc;y Ph&aacute;t T&agrave;i N&uacute;i 3 Th&acirc;n, Đại Lộc 3 th&acirc;n, Huyết Rồng, Phất Dụ Rồng<br />\r\nT&ecirc;n tiếng Anh: Dracaena Draco L<br />\r\nHọ: Dracaenaceae</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/cay-phat-tai-nui/\">C&acirc;y Ph&aacute;t T&agrave;i N&uacute;i</a>&nbsp;c&oacute; h&igrave;nh d&aacute;ng h&ugrave;ng vĩ, đạt chiều cao l&ecirc;n tới 5m, khi nh&igrave;n v&agrave;o sẽ thấy sự uy nghi&ecirc;m v&agrave; độc đ&aacute;o. Loại c&acirc;y n&agrave;y c&oacute; phần th&acirc;n gỗ với nhiều ph&acirc;n c&agrave;nh từ gốc, rễ phụ mọc từ th&acirc;n c&acirc;y.</p>\r\n\r\n<p><img alt=\"cây phát tài núi 3 thân\" src=\"https://webcaycanh.com/wp-content/uploads/2022/06/cay-phat-tai-nui-3-than-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<p>L&aacute; của c&acirc;y tập trung nhiều ở đỉnh với h&igrave;nh dạng thu&ocirc;n d&agrave;i 15 &ndash; 20cm, rộng 5 &ndash; 8cm, gốc c&oacute; bẹ &ocirc;m v&agrave;o th&acirc;n. Phiến của l&aacute;&nbsp;<a href=\"https://webcaycanh.com/cay-phat-tai-nui-3-than/\"><strong>C&acirc;y Ph&aacute;t T&agrave;i N&uacute;i 3 th&acirc;n</strong></a>&nbsp;c&oacute; m&agrave;u xanh lục đậm b&oacute;ng. B&ecirc;n cạnh đ&oacute;, hoa m&agrave;u đỏ cam c&oacute; dạng ch&ugrave;m với m&agrave;u v&agrave;ng nhỏ b&eacute; v&agrave; c&oacute; quả cầu h&igrave;nh nhỏ.</p>\r\n'),
(58, 'Cây trúc quân tử', 35000, 23, 'Một trong những loại cây rất thích hợp để trồng hàng rào, thay thế các vách ngăn không gian, vừa thẩm mỹ, vừa lại có tác dụng phòng thủy và giúp không khí trong lành hơn.', '<h2><strong>ĐẶC ĐIỂM CỦA C&Acirc;Y TR&Uacute;C QU&Acirc;N TỬ</strong></h2>\r\n\r\n<ul>\r\n	<li><strong>T&ecirc;n thường gọi:</strong>&nbsp;Tr&uacute;c qu&acirc;n tử</li>\r\n	<li><strong>T&ecirc;n tiếng Anh:</strong>&nbsp;Buluh Pagar</li>\r\n	<li><strong>Họ:</strong>&nbsp;Poaceae (Tr&uacute;c đ&agrave;o)</li>\r\n	<li><strong>T&ecirc;n khoa học:</strong>&nbsp;Bambusa multiplex</li>\r\n	<li><strong>Nguồn gốc</strong>: Nepal v&agrave; Trung Quốc</li>\r\n</ul>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2021/08/cay-truc-quan-tu.jpg\"><img alt=\"cây trúc quân tử\" src=\"https://webcaycanh.com/wp-content/uploads/2021/08/cay-truc-quan-tu-400x400.jpg\" style=\"height:400px; width:400px\" /></a></p>\r\n\r\n<p><em>C&acirc;y Tr&uacute;c Qu&acirc;n Tử</em></p>\r\n\r\n<p>C&acirc;y ph&aacute;t triển theo bụi mọc thưa với d&aacute;ng đứng thẳng. C&acirc;y c&oacute; chiều cao khoảng trung b&igrave;nh 1,5 &ndash; 3m. Th&acirc;n của c&acirc;y c&oacute; m&agrave;u v&agrave;ng rực tr&ocirc;ng rất bắt mắt. Tr&ecirc;n th&acirc;n c&oacute; nhiều đốt nhỏ giống với th&acirc;n tre, v&igrave; vậy khi mới nh&igrave;n nhiều người sẽ nhầm lẫn với loại c&acirc;y n&agrave;y.</p>\r\n\r\n<p>C&agrave;nh nh&aacute;nh của c&acirc;y c&oacute; đặc điểm l&agrave; mềm, cong v&agrave; thưa. L&aacute; tr&uacute;c c&oacute; dạng dải, m&agrave;u xanh b&oacute;ng, l&aacute; nhỏ kh&ocirc;ng cuống nhọn ở đầu. Th&acirc;n c&oacute; bẹ &ocirc;m, mọc th&agrave;nh ch&ugrave;m tr&ecirc;n mỗi đốt th&acirc;n. Lo&agrave;i c&acirc;y n&agrave;y cũng c&oacute; hoa v&agrave; quả n&ecirc;n được trồng trong nh&agrave; l&agrave;m c&acirc;y trang tr&iacute; rất nhiều.</p>\r\n\r\n<h2><strong>&Yacute; NGHĨA CỦA C&Acirc;Y TR&Uacute;C QU&Acirc;N TỬ</strong></h2>\r\n\r\n<p>Trong phong thủy,&nbsp;<a href=\"https://webcaycanh.com/cay-truc-quan-tu/\"><strong>C&acirc;y Tr&uacute;c Qu&acirc;n Tử</strong></a>&nbsp;được biết đến l&agrave; biểu tượng của sự may mắn, giải tỏa những điềm xấu trong cuộc sống h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>C&acirc;y Tr&uacute;c l&agrave; biểu tượng của người qu&acirc;n tử, đấng nam nhi, v&igrave; vậy trồng c&acirc;y n&agrave;y trong nh&agrave; c&oacute; &yacute; nghĩa l&agrave; chống lại kẻ gian, mang đến sự may mắn cũng như điềm l&agrave;nh cho gia chủ.</p>\r\n\r\n<p>Ngo&agrave;i ra c&acirc;y c&oacute; khả năng thanh lọc kh&ocirc;ng kh&iacute;, giảm bụi bẩn ở m&ocirc;i trường. V&igrave; vậy c&acirc;y được trồng nhiều trong nh&agrave;, h&agrave;ng r&agrave;o, tường nh&agrave; tạo lối đi&hellip;</p>\r\n\r\n<p>C&acirc;y l&agrave; m&oacute;n qu&agrave; &yacute; nghĩa trong cuộc sống h&agrave;ng ng&agrave;y. Những dịp như t&acirc;n gia, thăng chức hay mừng đỗ đạt trong thi cử, học h&agrave;nh&hellip; người ta thường chọn C&acirc;y Tr&uacute;c Qu&acirc;n Tử như một m&oacute;n qu&agrave; &yacute; nghĩa, mang đến niềm vui, may mắn v&agrave; thịnh vượng cho gia chủ. Đặc biệt trong sự nghiệp sẽ gi&uacute;p gia chủ thăng quan tiến chức nhanh hơn.</p>\r\n'),
(59, 'Sen đá cỏ tím', 15000, 24, 'Cây phát triển thành bụi mới màu sắc khá lạ mặt. Phù hợp để làm cây trồng chung tiểu cảnh, cây làm nền.', '<h2>ĐẶC ĐIỂM CỦA SEN Đ&Aacute; CỎ T&Iacute;M</h2>\r\n\r\n<p>T&ecirc;n thường gọi: Sen Đ&aacute; Cỏ T&iacute;m<br />\r\nHọ: Crassulaceae (Bỏng)<br />\r\nNguồn gốc: Ch&acirc;u Phi</p>\r\n\r\n<p><img alt=\"sen đá cỏ tím đẹp\" src=\"https://webcaycanh.com/wp-content/uploads/2022/02/sen-da-co-tim-dep-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<h2>&Yacute; NGHĨA CỦA SEN Đ&Aacute; CỎ T&Iacute;M</h2>\r\n\r\n<p>Sen đ&aacute; cỏ t&iacute;m c&oacute; l&aacute; mặt tr&ecirc;n m&agrave;u m&agrave;u xanh với c&aacute;c đốm li ti, mặt sau c&oacute; m&agrave;u t&iacute;m. L&aacute; của c&acirc;y thu&ocirc;n d&agrave;i về ngọn. Rất được ưa chuộng để trang tr&iacute; trong ph&ograve;ng học, qu&aacute;n cafe, c&acirc;y để ban c&ocirc;ng&hellip; Gi&uacute;p cho kh&ocirc;ng gian trong nh&agrave; được tươi m&aacute;t, dễ chịu. B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n c&oacute; lọc được một số chất độc hại trong kh&ocirc;ng kh&iacute; như: Nitor, benzen, formaldehyde, radon&hellip; Ngo&agrave;i những t&aacute;c dụng kể tr&ecirc;n, loại c&acirc;y n&agrave;y c&ograve;n l&agrave; biểu trưng cho t&igrave;nh y&ecirc;u m&atilde;nh liệt, t&igrave;nh bạn gắn b&oacute;. Trong phong thuỷ, n&oacute; c&ograve;n c&oacute; &yacute; nghĩa sung t&uacute;c v&agrave; đầm ấm của gia đ&igrave;nh.</p>\r\n\r\n<h2>XỬ L&Yacute; C&Acirc;Y KHI MUA VỀ</h2>\r\n\r\n<p>&ndash; Cắt bỏ hết c&aacute;c rễ nhỏ, chỉ để lại những rễ ch&iacute;nh nếu c&oacute;.</p>\r\n\r\n<p>&ndash; Xả to&agrave;n bộ gi&aacute; thể cũ, c&agrave;ng sạch th&igrave; c&acirc;y c&agrave;ng nhanh ra rễ mới.</p>\r\n\r\n<p>&ndash; Phơi c&acirc;y &iacute;t nhất 2 ng&agrave;y v&agrave; tốt nhất l&agrave; đợi c&acirc;y ra rễ mới, l&aacute; hơi mềm th&igrave; trồng.</p>\r\n\r\n<p><img alt=\"sen đá cỏ tím\" src=\"https://webcaycanh.com/wp-content/uploads/2022/02/sen-da-co-tim-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n'),
(60, 'Sen đá giva', 15000, 24, 'Cây Sen Đá Giva có cánh hơi bầu màu xanh, phù hợp làm cây phong thủy cho người mệnh mộc và hỏa. Cây phù hợp để ban công, cửa sổ, ngoài hiên, quán cà phê.', '<h2><strong>ĐẶC ĐIỂM CỦA SEN Đ&Aacute; GIVA</strong></h2>\r\n\r\n<p>T&ecirc;n thường gọi: Sen Đ&aacute; Giva<br />\r\nT&ecirc;n tiếng Anh: Echeveria Agavoides<br />\r\nHọ: Crassulaceae (Bỏng)<br />\r\nNguồn gốc: Mexico</p>\r\n\r\n<p><img alt=\"cây sen đá giva\" src=\"https://webcaycanh.com/wp-content/uploads/2022/04/cay-sen-da-giva-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<h2><strong>&Yacute; NGHĨA CỦA SEN Đ&Aacute; GIVA</strong></h2>\r\n\r\n<p><a href=\"https://webcaycanh.com/sen-da-giva/\">Sen đ&aacute; Giva</a>&nbsp;thể hiện cho t&igrave;nh y&ecirc;u s&acirc;u sắc, thuỷ chung v&agrave; nồng ch&aacute;y. C&acirc;y c&oacute; sắc xanh ph&ugrave; hợp l&agrave;m c&acirc;y phong thủy cho người mệnh mộc v&agrave; mệnh hỏa.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 592.6px; left: 273.163px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(61, 'Cây sen đá kim cương tím', 15000, 24, 'Một loài cây mới lạ với màu sắc đẹp. cây thường có kích thước từ 6-8cm. Mọc thành bụi khi trưởng thành.', '<h2><strong>ĐẶC ĐIỂM CỦA SEN Đ&Aacute; KIM CƯƠNG T&Iacute;M</strong></h2>\r\n\r\n<p>T&ecirc;n thường gọi: Sen Đ&aacute; Kim Cương T&iacute;m<br />\r\nT&ecirc;n tiếng Anh: Echeveria cubic frost<br />\r\nHọ: Crassulaceae (Bỏng)<br />\r\nNguồn gốc: Đ&agrave; Lạt</p>\r\n\r\n<p><img alt=\"cây sen đá kim cương tím\" src=\"https://webcaycanh.com/wp-content/uploads/2022/11/cay-sen-da-kim-cuong-tim-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<p>C&acirc;y được nh&acirc;n giống v&agrave; lai tạo ở Đ&agrave; Lạt ch&iacute;nh v&igrave; thế bạn sẽ &iacute;t thấy giống c&acirc;y n&agrave;y ở c&aacute;c nước kh&aacute;c. C&acirc;y thường ra hoa nhiều v&agrave;o cuối thu đầu đ&ocirc;ng.</p>\r\n\r\n<p>C&acirc;y Trưởng th&agrave;nh thường cũng kh&ocirc;ng to b&ocirc;ng như c&aacute;c loại kh&aacute;c m&agrave; sẽ ph&aacute;t triển th&ecirc;m c&aacute;c c&acirc;y con.</p>\r\n\r\n<h2><strong>&Yacute; NGHĨA CỦA SEN Đ&Aacute; KIM CƯƠNG T&Iacute;M</strong></h2>\r\n\r\n<p><a href=\"https://webcaycanh.com/cay-sen-da-kim-cuong-tim/\">Sen Đ&aacute; Kim Cương T&iacute;m</a>&nbsp;thể hiện cho t&igrave;nh y&ecirc;u s&acirc;u sắc, thuỷ chung v&agrave; mang &yacute; nghĩa tiền bạc. C&acirc;y c&oacute; sắc t&iacute;m, đỏ v&agrave; hồng chuyển xanh khi thiếu &aacute;nh s&aacute;ng, n&ecirc;n ph&ugrave; hợp l&agrave;m c&acirc;y phong thủy cho người mệnh mộc v&agrave; mệnh hỏa.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 655.4px; left: 383.413px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(62, 'Sen đá nuda', 15000, 24, 'Cây mọc thành bụi, dạng đài dễ sống và phát triển. Phù hợp làm cây trang trí, bày quán cafe, ban công...', '<h2><strong>ĐẶC ĐIỂM CỦA SEN Đ&Aacute; NUDA</strong></h2>\r\n\r\n<p>T&ecirc;n thường gọi: Sen Đ&aacute; Nuda<br />\r\nT&ecirc;n tiếng Anh: Echeveria Nuda<br />\r\nHọ: Crassulaceae (Bỏng)<br />\r\nNguồn gốc: Mexico</p>\r\n\r\n<h2><strong>&Yacute; NGHĨA CỦA SEN Đ&Aacute; NUDA</strong></h2>\r\n\r\n<p><a href=\"https://webcaycanh.com/sen-da-nuda/\">Sen đ&aacute; Nuda</a>&nbsp;thể hiện cho t&igrave;nh y&ecirc;u s&acirc;u sắc, thuỷ chung v&agrave; nồng ch&aacute;y. C&acirc;y c&oacute; sắc xanh ph&ugrave; hợp l&agrave;m c&acirc;y phong thủy cho người mệnh mộc v&agrave; mệnh hỏa.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 166px; left: 283.263px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(64, 'Sen đá cúc tím', 15000, 24, 'Cây sen đá hoa cúc tím với hình dạng giống bông hoa cúc, cây mang ý nghĩa cho sự trường thọ và một tình bạn, tình yêu vĩnh cửu...', '<h2><strong>ĐẶC ĐIỂM CỦA SEN Đ&Aacute; C&Uacute;C T&Iacute;M</strong></h2>\r\n\r\n<p>T&ecirc;n thường gọi: Sen Đ&aacute; C&uacute;c T&iacute;m<br />\r\nT&ecirc;n tiếng Anh: Echeveria cubic frost<br />\r\nHọ: Crassulaceae (Bỏng)<br />\r\nNguồn gốc: Mexico</p>\r\n\r\n<p><img alt=\"sen đá hoa cúc to\" src=\"https://webcaycanh.com/wp-content/uploads/2022/04/sen-da-hoa-cuc-to-400x400.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<h2><strong>&Yacute; NGHĨA CỦA SEN Đ&Aacute; C&Uacute;C T&Iacute;M</strong></h2>\r\n\r\n<p><a href=\"https://webcaycanh.com/sen-da-cuc-tim/\">Sen đ&aacute; c&uacute;c t&iacute;m</a>&nbsp;thể hiện cho t&igrave;nh y&ecirc;u s&acirc;u sắc, thuỷ chung v&agrave; nồng ch&aacute;y. C&acirc;y c&oacute; sắc t&iacute;m chuyển xanh khi thiếu &aacute;nh s&aacute;ng, n&ecirc;n ph&ugrave; hợp l&agrave;m c&acirc;y phong thủy cho người mệnh mộc v&agrave; mệnh hỏa.</p>\r\n\r\n<h2><strong>XỬ L&Yacute; C&Acirc;Y KHI MUA VỀ</strong></h2>\r\n\r\n<p>&ndash; Cắt bỏ hết c&aacute;c rễ nhỏ, chỉ để lại những rễ ch&iacute;nh nếu c&oacute;.</p>\r\n\r\n<p>&ndash; Xả to&agrave;n bộ gi&aacute; thể cũ, c&agrave;ng sạch th&igrave; c&acirc;y c&agrave;ng nhanh ra rễ mới.</p>\r\n\r\n<p>&ndash; Phơi c&acirc;y &iacute;t nhất 2 ng&agrave;y v&agrave; tốt nhất l&agrave; đợi c&acirc;y ra rễ mới, l&aacute; hơi mềm th&igrave; trồng.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 665.4px; left: 241.075px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(65, 'Cây mini monstera', 125000, 25, 'Cây Mini Monstera khá nổi bật với những chiếc lá xanh đậm và khuyết. Cây không cần chậu quá to chính vì thế cây rất phù hợp để trang trí những góc nhỏ trong nhà, quán cà phê, quà tặng...', '<h2><strong>ĐẶC ĐIỂM C&Acirc;Y MINI MONSTERA</strong></h2>\r\n\r\n<p><strong>T&ecirc;n thường gọi</strong>: Mini Monstera, Trầu B&agrave; L&aacute; Xẻ Form nhỏ</p>\r\n\r\n<p><strong>T&ecirc;n khoa học</strong>: Rhaphidophora Tetrasperma</p>\r\n\r\n<p><strong>Bộ (ordo)</strong>: Alismatales</p>\r\n\r\n<p><strong>Họ (familia)</strong>: Araceae (Họ r&aacute;y c&oacute; hoa)</p>\r\n\r\n<p><strong>Chi (genus)</strong>: Rhaphidophora</p>\r\n\r\n<p><strong>Lo&agrave;i (species)</strong>: R. tetrasperma</p>\r\n\r\n<p><strong>Nguồn gốc</strong>: Th&aacute;i Lan v&agrave; Malaysia</p>\r\n\r\n<p>Rhaphidophora Tetrasperma c&oacute; rất nhiều t&ecirc;n gọi như: mini monstera, ginny philodendron, mini-leaf philodendron, Trầu B&agrave; L&aacute; Xẻ Form nhỏ, Trầu B&agrave; Nam Mỹ b&eacute;.</p>\r\n\r\n<p>Mặc d&ugrave; &ldquo;<a href=\"https://webcaycanh.com/cay-mini-monstera/\"><em><strong>C&acirc;y Mini Monstera</strong></em></a>&rdquo; l&agrave; một biệt danh ho&agrave;n to&agrave;n đ&aacute;ng y&ecirc;u, nhưng tất cả những c&aacute;i t&ecirc;n đều kh&ocirc;ng phải. Tetrasperma kh&ocirc;ng phải l&agrave; Monstera hay Philodendron. Tuy nhi&ecirc;n, n&oacute; l&agrave; một th&agrave;nh vi&ecirc;n của c&ugrave;ng một họ, Araceae, thường được gọi l&agrave; &ldquo;aroids&rdquo;.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2021/08/cay-mini-monstera-leo.jpg\"><img alt=\"cây mini monstera leo\" src=\"https://webcaycanh.com/wp-content/uploads/2021/08/cay-mini-monstera-leo-318x400.jpg\" style=\"height:400px; width:318px\" /></a></p>\r\n\r\n<p><em>C&acirc;y Mini Monstera c&oacute; thể leo v&agrave; b&aacute;m</em></p>\r\n\r\n<p>Nh&igrave;n bao qu&aacute;t th&igrave; l&aacute; c&oacute; h&igrave;nh tr&aacute;i tim với c&aacute;c vết xẻ tr&ecirc;n l&aacute; so le với nhau, c&acirc;y thuộc dạng dễ kh&iacute; ở tr&ecirc;n mỗi đốt c&acirc;y sẽ mọc th&ecirc;m rễ để c&oacute; thể b&aacute;m.</p>\r\n\r\n<p>C&acirc;y thuộc họ R&aacute;y n&ecirc;n cũng c&oacute; đơm hoa v&agrave; kết quả, quả của c&acirc;y giống như ng&oacute;n tay.</p>\r\n\r\n<h2><strong>T&Aacute;C DỤNG C&Acirc;Y MINI MONSTERA</strong></h2>\r\n\r\n<ul>\r\n	<li><strong>Lọc kh&iacute;</strong>: D&ograve;ng họ r&aacute;y n&oacute;i chung v&agrave; Mini Monstera n&oacute;i ri&ecirc;ng đều c&oacute; khả năng lọc kh&iacute; rất tốt, hấp thụ những chất c&oacute; thể g&acirc;y hại ở thể hơi như formaldehydes v&agrave; c&aacute;c chất h&oacute;a học ở thể hơi.</li>\r\n	<li><strong>Trang tr&iacute;</strong>: Kh&aacute;c với đa phần loại c&acirc;y cảnh kh&aacute;c th&igrave; Mini Monstera c&oacute; thể leo v&agrave; tạo ra được những mảnh xanh ấn tượng v&agrave; diện t&iacute;ch chậu trồng kh&ocirc;ng cần lớn.</li>\r\n</ul>\r\n'),
(66, 'Cây trầu bà lá lỗ', 125000, 25, 'Cây Trầu Bà Lá Lỗ có lá rất đặc biệt mà không giống với loại cây nào. Chính vì thế cây thường được chọn để tạo sự nổi bật và đặc sắc như trang trí nội thất, văn phòng, quà tặng, người chơi hệ lá...', '<h2><strong>ĐẶC ĐIỂM CỦA C&Acirc;Y TRẦU B&Agrave; L&Aacute; LỖ</strong></h2>\r\n\r\n<p><a href=\"https://webcaycanh.com/cay-trau-ba-la-lo/\"><em><strong>C&acirc;y Trầu B&agrave; L&aacute; Lỗ</strong></em></a>&nbsp;c&oacute; t&ecirc;n khoa học l&agrave; Monstera obliqua var.expilata, l&agrave; thực vật c&oacute; hoa trong họ R&aacute;y (Araceae). Lo&agrave;i n&agrave;y được Miq. m&ocirc; tả khoa học đầu ti&ecirc;n năm 1845.</p>\r\n\r\n<p>Th&acirc;n v&agrave; nh&aacute;nh l&agrave; một, dạng th&acirc;n mềm chia l&agrave;m nhiều đốt, ở mỗi đốt c&oacute; thể mọc rễ v&agrave; t&aacute;ch ra l&agrave;m c&acirc;y kh&aacute;c. C&acirc;y thuộc th&acirc;n leo b&aacute;m bằng rễ, v&agrave; sẽ rủ nếu kh&ocirc;ng c&oacute; vật để b&aacute;m.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2021/04/la-non-cay-trau-ba-la-lo.jpg\"><img alt=\"Lá non Cây Trầu Bà Lá Lỗ\" src=\"https://webcaycanh.com/wp-content/uploads/2021/04/la-non-cay-trau-ba-la-lo-300x400.jpg\" style=\"height:400px; width:300px\" /></a></p>\r\n\r\n<p><em>L&aacute; non cuộn tr&ograve;n</em></p>\r\n\r\n<p>L&aacute; c&acirc;y c&oacute; m&agrave;u xanh tr&ecirc;n l&aacute; c&oacute; nhiều lỗ thủng to nhỏ kh&aacute;c nhau, nh&igrave;n qua c&oacute; thể tưởng l&agrave; l&aacute; bị s&acirc;u ăn hay bị r&aacute;ch.&nbsp;<strong>L&aacute; c&acirc;y Trầu B&agrave; Lỗ</strong>&nbsp;c&oacute; h&igrave;nh bầu dục thon dần về ph&iacute;a đầu l&aacute;, d&agrave;i từ 5-12cm, rộng 4-6cm. L&aacute; mới mọc sẽ được cuộn lại c&oacute; m&agrave;u xanh non v&agrave; c&oacute; thể kh&ocirc;ng bị thủng về sau m&agrave;u xanh sẽ xẫm dần khi trưởng th&agrave;nh v&agrave; c&aacute;ch lỗ tr&ecirc;n l&aacute; sẽ to ra.</p>\r\n\r\n<h2><strong>C&Ocirc;NG DỤNG TRẦU B&Agrave; L&Aacute; LỖ</strong></h2>\r\n\r\n<p>C&acirc;y c&oacute; khả năng loại bỏ chất g&acirc;y ung thư formaldehydes v&agrave; nhiều chất h&oacute;a học dễ bay hơi kh&aacute;c, l&agrave; loại lọc kh&ocirc;ng kh&iacute; rất tốt.</p>\r\n\r\n<p><img alt=\"cay-trau-ba-la-lo-leo\" src=\"https://webcaycanh.com/wp-content/uploads/2021/04/cay-trau-ba-la-lo-leo-300x400.jpg\" style=\"height:400px; width:300px\" /></p>\r\n\r\n<p>D&ugrave;ng l&agrave;m c&acirc;y xanh trang tr&iacute; v&agrave; nhả oxi trong nh&agrave;. Mang đến kh&ocirc;ng gian tươi m&aacute;t, trong l&agrave;nh v&agrave; dễ chịu</p>\r\n\r\n<p>&Yacute; nghĩa phong thủy: C&acirc;y thuộc hệ mộc rất ph&ugrave; hợp l&agrave;m&nbsp;<a href=\"https://webcaycanh.com/cay-canh-phong-thuy/\" target=\"_blank\">c&acirc;y phong thủy</a>&nbsp;cho người mệnh Mộc v&agrave; mệnh Hỏa. Gi&uacute;p k&iacute;ch th&iacute;ch vận may, kh&iacute; tốt trong nh&agrave;, mang lại t&agrave;i lộc ph&uacute; qu&yacute; cho người trồng.</p>\r\n\r\n<p>&nbsp;</p>\r\n'),
(67, 'Cây bình an', 149000, 25, 'Cây Bình An loại cây treo có ý nghĩa phong thủy mang đến sự bình an và may mắn, thường được treo trước cửa nhà để các thành viên trong nhà luôn được may mắn và bình an.', '<h2><strong>ĐẶC ĐIỂM CỦA C&Acirc;Y B&Igrave;NH AN</strong></h2>\r\n\r\n<p><strong>T&ecirc;n</strong>: C&acirc;y B&igrave;nh Anh</p>\r\n\r\n<p><strong>T&ecirc;n khoa học</strong>: Peperomia angulata</p>\r\n\r\n<p><strong>Họ</strong>: Hồ Ti&ecirc;u</p>\r\n\r\n<p><strong>Nguồn gốc</strong>: Ch&acirc;u Phi</p>\r\n\r\n<p>Peperomia l&agrave; một trong hai chi lớn thuộc Họ Hồ ti&ecirc;u, với hơn 1000 lo&agrave;i được ghi nhận. Phần lớn l&agrave; c&acirc;y nhỏ mọc tr&ecirc;n gỗ mục. C&aacute;c lo&agrave;i trong chi n&agrave;y ph&acirc;n bố khắp c&aacute;c v&ugrave;ng nhiệt đới v&agrave; cận nhiệt đới tr&ecirc;n to&agrave;n thế giới, d&ugrave; tập trung ở Trung Mỹ v&agrave; bắc Nam Mỹ. C&oacute; khoảng 17 lo&agrave;i ph&acirc;n bố ở ch&acirc;u Phi.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2020/08/la-cay-binh-an.jpg\"><img alt=\"Lá cây bình an\" src=\"https://webcaycanh.com/wp-content/uploads/2020/08/la-cay-binh-an-300x400.jpg\" style=\"height:400px; width:300px\" /></a></p>\r\n\r\n<p><em>C&acirc;y b&igrave;nh an c&oacute; rất nhiều rễ ở c&aacute;c nh&aacute;nh để b&aacute;m</em></p>\r\n\r\n<p><a href=\"https://webcaycanh.com/cay-binh-an/\"><strong>C&acirc;y B&igrave;nh An</strong></a>&nbsp;nổi bật v&agrave; thu h&uacute;t người đối diện bởi h&igrave;nh d&aacute;ng nhỏ nhắn nhưng đặc biệt kh&aacute;c lạ. C&acirc;y mọc th&agrave;nh bụi, thường được trồng trong lọ để b&agrave;n hoặc treo l&ecirc;n g&oacute;c nh&agrave; hay h&agrave;nh lang.</p>\r\n\r\n<p>L&aacute; c&acirc;y h&igrave;nh bầu dục, mọc đối xứng qua th&acirc;n, c&oacute; c&aacute;c đường viền m&agrave;u trắng chạy dọc tr&ecirc;n mặt l&aacute; rất bắt mắt. Mỗi chiếc l&aacute; c&oacute; khoảng 3-4 đường viền t&ugrave;y k&iacute;ch thước, thuộc loại&nbsp;<a href=\"https://webcaycanh.com/cay-day-leo/\" target=\"_blank\">c&acirc;y d&acirc;y leo</a>&nbsp;b&aacute;m bằng rễ n&ecirc;n ở mỗi đoạn th&acirc;n c&oacute; l&aacute; khi trưởng th&agrave;nh đều c&oacute; rễ mọc ra.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2020/08/hoa-cay-binh-an.jpg\"><img alt=\"Hoa cây bình an\" src=\"https://webcaycanh.com/wp-content/uploads/2020/08/hoa-cay-binh-an-335x400.jpg\" style=\"height:400px; width:335px\" /></a></p>\r\n\r\n<p><em>Hoa của c&acirc;y B&igrave;nh An</em></p>\r\n\r\n<p>C&acirc;y c&oacute; hoa nhỏ d&agrave;i giống như hoa của c&acirc;y Ti&ecirc;u, hoa ra quanh năm khi điều kiện đủ nắng.</p>\r\n\r\n<h2><strong>&Yacute; NGHĨA PHONG THỦY CỦA C&Acirc;Y B&Igrave;NH AN</strong></h2>\r\n\r\n<p>&Yacute; nghĩa phong thủy của c&acirc;y cũng giống như c&aacute;i t&ecirc;n. B&igrave;nh An c&oacute; nghĩa l&agrave; cuộc sống kh&ocirc;ng phải suy nghĩ nhiều, gặp nguy biến an, tiền bạc th&igrave; ổn định, đi đ&acirc;u cũng gặp may mắn.</p>\r\n\r\n<p>C&acirc;y c&oacute; m&agrave;u l&aacute; xanh nổi bật đại diện cho h&agrave;nh mộc n&ecirc;n những gia chủ c&oacute; h&agrave;nh Mộc v&agrave; h&agrave;nh Hỏa l&agrave; cực kỳ ph&ugrave; hợp để tăng th&ecirc;m vận kh&iacute;.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 1294px; left: 407.863px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(68, 'Cây lan hạt dưa', 149000, 25, 'Những loại cây treo luôn được nhiều người ưa thích vì nó giúp tận dụng không gian bên trên ít sử dụng đến. Cây Lan Hạt Dưa cũng là một lựa chọn khá ổn nếu bạn đang tìm cây treo.', '<p>&nbsp;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 39px; left: 82.8625px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(69, 'Cây thường xuân', 149000, 25, 'Cây Thường Xuân mang ý nghĩa phong thủy xua đuổi tà ma, xóa tan âm khí, vượng dương khí mang đến bình an và may mắn cho gia chủ. Cây phù hợp để trang trí quán cà phê, nhà hàng, khách sạn, phòng họp...', '<h2><strong>ĐẶC ĐIỂM CỦA C&Acirc;Y THƯỜNG XU&Acirc;N</strong></h2>\r\n\r\n<p><a href=\"https://webcaycanh.com/cay-thuong-xuan/\"><em><strong>C&acirc;y Thương Xu&acirc;n</strong></em></a>&nbsp;c&oacute; t&ecirc;n khoa học:&nbsp;Hedera helix l&agrave; một lo&agrave;i thực vật thuộc chi D&acirc;y thường xu&acirc;n (Hedera), Họ Cuồng cuồng (Araliaceae). C&acirc;y c&oacute; nguồn gốc ở ch&acirc;u &Acirc;u v&agrave; T&acirc;y &Aacute;, l&agrave; lo&agrave;i c&acirc;y leo, thường xanh. Ch&uacute;ng c&oacute; khả năng sinh sống v&agrave; lan tr&ecirc;n bề mặt dốc cao tới 20-30 m&eacute;t. Ở nhiều nơi, ch&uacute;ng được trồng để tạo m&agrave;u xanh v&agrave; để l&agrave;m h&agrave;ng r&agrave;o.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2017/04/cay-thuong-xuan-treo.jpg\"><img alt=\"cay-thuong-xuan-treo\" src=\"https://webcaycanh.com/wp-content/uploads/2017/04/cay-thuong-xuan-treo-300x200.jpg\" style=\"height:200px; width:300px\" /></a></p>\r\n\r\n<p><em>L&aacute; c&acirc;y Thường Xu&acirc;n</em></p>\r\n\r\n<p>Tr&ecirc;n th&acirc;n d&acirc;y của Thường Xu&acirc;n c&oacute; nhiều đốt ở mỗi sẽ c&oacute; l&aacute; v&agrave; rễ ph&aacute;t triển để gi&uacute;p c&acirc;y b&aacute;m chắc, đối với c&agrave;nh non thường c&oacute; l&ocirc;ng phủ dạng vảy c&aacute;, l&aacute; non c&oacute; m&agrave;u xanh nhạt v&agrave; đậm dần khi trưởng th&agrave;nh. C&acirc;y thường c&oacute; hoa v&agrave;o đầu thu, hoa nhỏ c&oacute; 5 c&aacute;nh, c&aacute;nh hoa chụm lại như h&igrave;nh chiếc &ocirc;, m&agrave;u v&agrave;ng nhạt, hương thơm thoang thoảng nhẹ nh&agrave;ng. Sau khi c&acirc;y ra hoa từ 1 &ndash; 2 th&aacute;ng sẽ th&agrave;nh quả, quả&nbsp;<strong>Thường Xu&acirc;n</strong>&nbsp;c&oacute; m&agrave;u hơn đỏ hoặc m&agrave;u v&agrave;ng. Nhưng thường c&acirc;y Thường Xu&acirc;n được trồng trong nh&agrave; &iacute;t c&oacute; hoa v&agrave; quả, nếu c&acirc;y được trồng ngo&agrave;i vườn, trồng l&agrave;m h&agrave;ng r&agrave;o được phơi nắng trực tiếp th&igrave; khả năng ra hoa v&agrave; quả sẽ cao hơn.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2017/04/cay-thuong-xuan-uon.jpg\"><img alt=\"cay-thuong-xuan-uon\" src=\"https://webcaycanh.com/wp-content/uploads/2017/04/cay-thuong-xuan-uon-300x300.jpg\" style=\"height:300px; width:300px\" /></a></p>\r\n\r\n<p><em>C&acirc;y Thường Xu&acirc;n dễ uốn để tạo h&igrave;nh</em></p>\r\n\r\n<p>Ngo&agrave;i ra c&acirc;y Thường xu&acirc;n c&ograve;n được nhiều người d&ugrave;ng l&agrave;m thuốc chữa nhiều loại bệnh kh&aacute;c nhau như giải độc, hạ đường huyết</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 35px; left: 359.225px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(70, 'Bộ đĩa tự tưới', 6000, 27, 'Hiện tại bộ đĩa cây tự tưới chỉ có 1 cỡ duy nhất đường kính 12cm, như vậy nó sẽ phù hợp với những chậu cây văn phòng có đường kính nhỏ hơn 12cm, bàn làm việc của bạn sẽ luôn sạch sẽ và không còn phải lo việc tưới cây', '<h2><strong>HƯỚNG DẪN D&Ugrave;NG&nbsp;ĐĨA TỰ TƯỚI</strong></h2>\r\n\r\n<p>Ch&uacute;ng ta c&ugrave;ng kết nối d&acirc;y bấc với chậu c&acirc;y, v&ocirc; c&ugrave;ng đơn giản, bạn lấy d&acirc;y bấc để dưới đ&aacute;y chậu, d&ugrave;ng 1 chiếc que nhỏ lấy dấu độ s&acirc;u trong khoảng 1/3 &ndash; &frac14; chiều cao của chậu rồi ấn d&acirc;y v&ocirc;, v&agrave; đặt chậu c&acirc;y v&agrave;o đĩa.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2019/09/ket-noi-day-bac-voi-chau-cay.jpg\"><img alt=\"ket-noi-day-bac-voi-chau-cay\" src=\"https://webcaycanh.com/wp-content/uploads/2019/09/ket-noi-day-bac-voi-chau-cay-400x224.jpg\" style=\"height:224px; width:400px\" /></a></p>\r\n\r\n<p><em>Kết nối d&acirc;y bấc với chậu c&acirc;y</em></p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2019/09/do-nuoc-vao-dia.jpg\"><img alt=\"do-nuoc-vao-dia\" src=\"https://webcaycanh.com/wp-content/uploads/2019/09/do-nuoc-vao-dia-400x227.jpg\" style=\"height:227px; width:400px\" /></a></p>\r\n\r\n<p><em>Đổ nước v&agrave;o&nbsp;đĩa</em></p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2019/09/dia-tu-tuoi.jpg\"><img alt=\"dia-tu-tuoi\" src=\"https://webcaycanh.com/wp-content/uploads/2019/09/dia-tu-tuoi-400x300.jpg\" style=\"height:300px; width:400px\" /></a></p>\r\n\r\n<p><em>Đặt c&acirc;y v&agrave;o&nbsp;đĩa nữa l&agrave; xong</em></p>\r\n\r\n<p>Đối với nhưng c&acirc;y kh&ocirc;ng ưa qu&aacute; ẩm như:&nbsp;<a href=\"https://webcaycanh.com/cay-kim-ngan/\">Kim Ng&acirc;n</a>, Kim Tiền, Vạn Lộc, Lưỡi Hổ, Trường Sinh.. th&igrave; 1 tuần bạn đổ nước 1 lần, v&iacute; dụ cứ thứ 2 h&agrave;ng tuần bạn đổ nước v&agrave;o đĩa. Đối với c&acirc;y ưa ẩm như: C&acirc;y Cẩm Nhung, c&acirc;y Trầu B&agrave;, Lan &yacute;, Cỏ Đồng Tiền &hellip; th&igrave; cứ khi n&agrave;o bạn thấy đĩa hết nước th&igrave; đổ v&agrave;o l&agrave; được.</p>\r\n\r\n<p>Đĩa tự tưới hiện nay mới c&oacute; duy nhất một cỡ đĩa với đường k&iacute;nh l&agrave; 12cm, chiều cao 2,6cm, ph&ugrave; hợp với c&aacute;c chậu c&acirc;y để b&agrave;n c&oacute; đường k&iacute;nh nhỏ hơn 12cm</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 23px; left: 332.312px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(71, 'Đất dinh dưỡng', 29000, 27, 'Bao có thể tích là 20dm3 khoảng 10kg vì đất khi ẩm và khô khối lượng lệch nhau rất nhiều, nên khối lượng chỉ là tham khảo để bạn dễ hình dung. Phù hợp trồng cây cảnh, hoa, cây rau...', '<h2><strong>ĐẤT DINH DƯỠNG TRỒNG C&Acirc;Y L&Agrave; G&Igrave;?</strong></h2>\r\n\r\n<p>Thật ra n&oacute; cũng l&agrave; đất thịt nhưng sẽ được cho v&agrave;o m&aacute;y say nhỏ. Sau đ&oacute; trộn th&ecirc;m v&agrave;o đất c&aacute;c chất dinh dưỡng, ph&acirc;n b&oacute;n v&agrave; chất l&agrave;m đất tơi xốp đất như: Trấu hun, trấu ủ mục, sơ dừa, ph&acirc;n hữu cơ&hellip;</p>\r\n\r\n<p>Gi&uacute;p hoa, rau v&agrave; c&acirc;y cảnh c&oacute; thể dễ d&agrave;ng hấp thụ chất dinh dưỡng. Rễ c&acirc;y kh&ocirc;ng bị b&iacute; đất kh&ocirc;ng bị n&eacute;n chặt khi tưới nhiều lần.</p>\r\n\r\n<h2><strong>ĐẶC ĐIỂM ĐẤT DINH DƯỠNG</strong></h2>\r\n\r\n<p><strong>T&ecirc;n</strong>: Đất dinh dưỡng, đất sạch, đất dinh dưỡng trồng c&acirc;y</p>\r\n\r\n<p><strong>Khối lượng</strong>: 20dm3 (khoảng 10kg)</p>\r\n\r\n<p><strong>Nơi sản xuất</strong>: Việt Nam</p>\r\n\r\n<p>V&igrave; đất khi ướt v&agrave; kh&ocirc; sẽ c&oacute; khối lượng ch&ecirc;ch nhau đ&aacute;ng kể n&ecirc;n n&oacute; được t&iacute;nh theo thể t&iacute;ch v&agrave; ước chừng khoảng 10kg để mọi người dễ h&igrave;nh dung.</p>\r\n\r\n<h3>C&aacute;ch d&ugrave;ng</h3>\r\n\r\n<p>Sẽ được chia l&agrave;m 2 loại l&agrave;&nbsp;<a href=\"https://webcaycanh.com/\">c&acirc;y cảnh</a>, hoa, c&acirc;y trồng v&agrave; rau.</p>\r\n\r\n<p>Đối với c&acirc;y cảnh, hoa bạn đổ một lớp đất ph&iacute;a b&ecirc;n dưới để chừa lại phần từ mặt đất đến mặt chậu bằng với chiều cao của bầu c&acirc;y cảnh, c&acirc;y hoa. Sau đ&oacute; đặt c&acirc;y v&agrave;o rồi đổ tiếp l&ecirc;n tr&ecirc;n bề mặt để kh&ocirc;ng c&ograve;n khe hở. Tiếp n&oacute; l&agrave; bạn c&oacute; thể tưới v&agrave; chăm s&oacute;c.</p>\r\n\r\n<p><img alt=\"cách trồng cây bằng đất dinh dưỡng\" src=\"https://webcaycanh.com/wp-content/uploads/2022/11/cach-trong-cay-bang-dat-dinh-duong-400x268.jpg\" style=\"height:268px; width:400px\" /></p>\r\n\r\n<p>Đối với rau c&acirc;y mầm. Bạn đổ đầy đất v&agrave;o chậu sau đ&oacute; san bằng rắc hạt hoặc d&ugrave;ng xẻng nhỏ bới ra cho c&acirc;y rau giống, hoặc hạt mầm. Sau đ&oacute; đ&oacute; lấp lại v&agrave; tưới ẩm.</p>\r\n\r\n<p>Đất được t&iacute;nh l&agrave; trung t&iacute;nh v&igrave; vậy đối với những c&acirc;y ưa đất c&oacute; độ PH cao hay thấp hơn b&igrave;nh thường th&igrave; bạn h&atilde;n trộn bổ sung c&aacute;c th&agrave;nh phần để tăng hoặc giảm PH của đất.</p>\r\n\r\n<h3>Trồng bằng đất dinh dưỡng sẽ kh&ocirc;ng phải b&oacute;n ph&acirc;n?</h3>\r\n\r\n<p>C&oacute; thể đ&oacute; cũng l&agrave; c&acirc;u hỏi thắc mắc nhiều người. Nhưng c&acirc;u trả lời vẫn phải b&oacute;n ph&acirc;n v&igrave; kh&ocirc;ng c&oacute; loại đất n&agrave;o khi tưới nhiều v&agrave; c&acirc;y hấp thụ dinh dưỡng trong đất m&agrave; c&oacute; thể d&ugrave;ng m&atilde;i.</p>\r\n\r\n<p>Thay v&igrave; việc phải thay đất th&igrave; bạn c&oacute; thể b&oacute;n hay tưới ph&acirc;n định kỳ cho đất gi&uacute;p đất lu&ocirc;n đủ dinh dưỡng. V&iacute; dụ bạn c&oacute; thể tưới bằng nước vo gạo để qua đ&ecirc;m, nước tiểu pha lo&atilde;ng, hoặc c&aacute;c loại ph&acirc;n hữu cơ như tr&ugrave;n quế, ph&acirc;n b&ograve;, ph&acirc;n d&ecirc;&hellip;</p>\r\n'),
(72, 'Phụ kiện gấu trúc', 15000, 27, 'Gấu có kích thước từ 3,5 đến 5cm tùy vào hình dáng mà có độ dài rộng khác nhau. Phù hợp để trang trí góc nhỏ làm việc, học tập và tiểu cảnh cây.', '<p>Gấu tr&uacute;c được biết đến l&agrave; một loại động vật mập mạp v&agrave; dễ thương. Ch&iacute;nh v&igrave; thế m&agrave; bạn sẽ hay thấy c&aacute;c phụ kiện v&agrave; th&uacute; b&ocirc;ng đều c&oacute; h&igrave;nh gấu. C&aacute;c bạn muốn g&oacute;c l&agrave;m việc hay c&aacute;c tiểu cảnh c&acirc;y nhỏ để b&agrave;n của m&igrave;nh th&ecirc;m sinh động th&igrave; c&oacute; thể sử dụng đến&nbsp;<a href=\"https://webcaycanh.com/phu-kien-gau-truc/\">phụ kiện gấu tr&uacute;c</a>&nbsp;n&agrave;y.</p>\r\n\r\n<p><img alt=\"phụ kiện gấu trúc\" src=\"https://webcaycanh.com/wp-content/uploads/2022/10/phu-kien-gau-truc-5-400x259.jpg\" style=\"height:259px; width:400px\" /></p>\r\n\r\n<p><em>Gấu c&oacute; 8 kiểu k&iacute;ch thước từ 3,5 &ndash; 5cm</em></p>\r\n\r\n<p><img alt=\"phụ kiện gấu trúc\" src=\"https://webcaycanh.com/wp-content/uploads/2022/10/phu-kien-gau-truc-1-400x260.jpg\" style=\"height:260px; width:400px\" /></p>\r\n\r\n<p><em>H&igrave;nh ảnh gấu khi cầm tr&ecirc;n tay</em></p>\r\n\r\n<p><img alt=\"phụ kiện gấu trúc\" src=\"https://webcaycanh.com/wp-content/uploads/2022/10/phu-kien-gau-truc-2-400x258.jpg\" style=\"height:258px; width:400px\" /></p>\r\n\r\n<p><em>C&aacute;c tư thế của gấu tr&uacute;c</em></p>\r\n\r\n<h2><strong>C&Aacute;C SỬ DỤNG PHỤ KIỆN GẤU TR&Uacute;C</strong></h2>\r\n\r\n<p>Để sự dụng phụ kiện n&agrave;y ta c&oacute; 2 loại. Một l&agrave; c&aacute;c bạn đặt phụ kiện tr&ecirc;n mặt phẳng hoặc mặt cứng th&igrave; ta c&oacute; thể d&ugrave;ng keo nến gắn trực tiếp sản phẩm v&agrave;o vị tr&iacute; mong muốn.</p>\r\n\r\n<p>Hai l&agrave; bạn muốn gắn phụ kiện tr&ecirc;n bề mặt kh&ocirc;ng bằng phẳng m&agrave; c&oacute; độ n&uacute;m. Bạn d&ugrave;ng keo nến gắn sản phẩm v&agrave;o que nhọn rồi cắm xuống th&igrave; sẽ cố theo vị tr&iacute; bạn mong muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 1030px; left: 363.438px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(73, 'Bình tưới cây sen đá', 15000, 27, 'Tưới cây cho sen đá hay cây văn phòng rất hay bị tràn đất ra ngoài, cây đọng nước lên kẽ lá. Khiến cây bị thối hỏng. Bình tưới cây sẽ giúp bạn khắc phục điều này', '<p>C&acirc;y Sen Đ&aacute; kh&ocirc;ng ưa nước v&agrave; nếu ở thời tiết ẩm, l&acirc;u kh&ocirc; hay nắng n&oacute;ng. Nếu c&acirc;y bị đọng nước ở phần l&otilde;i l&aacute; th&igrave; rất kh&oacute; tho&aacute;t nước. Buộc bạn phải xịt hoặc thổi nước rất mất c&ocirc;ng. Để khắc phục điều n&agrave;y bạn chỉ cần mua b&igrave;nh tưới&nbsp;<a href=\"https://webcaycanh.com/cay-canh-sen-da/\">c&acirc;y Sen Đ&aacute;</a>&nbsp;l&agrave; c&oacute; thể tưới nước dễ d&agrave;ng.</p>\r\n\r\n<p>Ngo&agrave;i ra b&igrave;nh cũng c&oacute; thể d&ugrave;ng để tưới cho những c&acirc;y nhỏ để b&agrave;n ở văn ph&ograve;ng. Gi&uacute;p mỗi lần tưới trở l&ecirc;n dễ d&agrave;ng, kh&ocirc;ng bị tr&agrave;n đất ra b&agrave;n.</p>\r\n\r\n<p><img alt=\"bình tưới sen đá 500ml\" src=\"https://webcaycanh.com/wp-content/uploads/2022/10/binh-tuoi-sen-da-500ml.jpg\" style=\"height:320px; width:320px\" /></p>\r\n\r\n<h2><strong>C&Aacute;CH SỬ DỤNG B&Igrave;NH TƯỚI C&Acirc;Y SEN Đ&Aacute;</strong></h2>\r\n\r\n<p>Bạn chỉ cần vặn nắp v&agrave; đổ nước v&agrave;o trong b&igrave;nh. Nếu bạn d&ugrave;ng nước m&aacute;y th&igrave; c&oacute; thể xả đầy b&igrave;nh rồi để đấy, để kh&iacute; clo trong nước bay hết.</p>\r\n\r\n<p>Sau khi nước được đổ v&agrave;o b&igrave;nh ta chỉ cần b&oacute;p nhẹ, nước sẽ ra ở đầu v&ograve;i theo hướng bạn mong muốn.</p>\r\n\r\n<p><strong>Lưu &yacute;</strong>: Khi tưới bạn n&ecirc;n b&oacute;p nhẹ để đất đỡ bị xối.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 469.4px; left: 404.038px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(74, 'Trấu hun lẫn nguyên cánh', 149000, 27, 'Giúp đất trồng giữ ẩm và tơi xốp. Giá thành rẻ rất phù hợp để trộn cùng với đất thịt để trồng rau, trồng cây cảnh...', '<h2><strong>TRẤU HUN LẪN NGUY&Ecirc;N C&Aacute;NH L&Agrave; G&Igrave;?</strong></h2>\r\n\r\n<p>Trấu th&igrave; c&oacute; lẽ tất cả mọi người đều biết, nhưng trấu hun lẫn nguy&ecirc;n c&aacute;nh th&igrave; hơi kh&aacute;c một ch&uacute;t.</p>\r\n\r\n<p>Trấu hun hay c&ograve;n gọi l&agrave; biochar, than sinh học, than trấu&hellip;Được h&igrave;nh th&agrave;nh trong qu&aacute; tr&igrave;nh đốt trấu tươi (trấu sống) ở điều kiện thiếu oxi gi&uacute;p trấu kh&ocirc;ng bị vụn v&agrave; mất chất dinh dưỡng. V&igrave; trong qu&aacute; tr&igrave;nh hun ở nhiệt độ cao n&ecirc;n hầu hết c&aacute;i vi khuẩn đều được triệt ti&ecirc;u.</p>\r\n\r\n<p>Trầu hun lẫn nguy&ecirc;n c&aacute;nh l&agrave; loại trấu được ch&aacute;y chỉ gần hết gi&uacute;p trong trấu c&oacute; cả c&aacute;nh sống v&agrave; ch&iacute;n. Đều được diệt hết vi khuẩn nhờ nhiệt độ. V&agrave; vỏ trấu được giữ nguy&ecirc;n h&igrave;nh dạng.</p>\r\n\r\n<h2><strong>T&Aacute;C DỤNG CỦA TRẤU HUN LẪN</strong></h2>\r\n\r\n<p>Trấu hun lẫn th&agrave;nh phần dinh dưỡng chủ yếu l&agrave; carbohydrat v&agrave; kali. Trong qu&aacute; tr&igrave;nh trồng th&igrave; trấu hun ch&iacute;n sẽ được ph&acirc;n hủy trước v&agrave; trấu hun sống được ph&acirc;n hủy sau.</p>\r\n\r\n<p>Gi&uacute;p đất c&oacute; dinh dưỡng trong một khoảng thời gian d&agrave;i. Tuy nhi&ecirc;n n&ecirc;n trộn th&ecirc;m c&ugrave;ng c&aacute;c chất dinh dưỡng kh&aacute;c để c&acirc;y c&oacute; thể đầy đủ chất dinh dưỡng.</p>\r\n\r\n<p><img alt=\"tác dụng trấu hun lẫn\" src=\"https://webcaycanh.com/wp-content/uploads/2022/11/tac-dung-trau-hun-lan-400x207.jpg\" style=\"height:207px; width:400px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra&nbsp;<a href=\"https://webcaycanh.com/trau-hun-lan-nguyen-canh/\"><strong>trấu hun lẫn nguy&ecirc;n c&aacute;nh</strong></a>&nbsp;c&ograve;n gi&uacute;p đất tươi xốp, c&oacute; độ ẩm ph&ugrave; hợp v&igrave; trấu giữ nước vừa phải gi&uacute;p nước tho&aacute;t ra từ từ kh&ocirc;ng l&agrave;m c&acirc;y bị &uacute;ng m&agrave; cung cấp lượng nước vừa đủ khi c&acirc;y cần.</p>\r\n\r\n<p>Một t&aacute;c dụng đ&aacute;ng kể nữa l&agrave; khả năng lọc nước. Trấu hun ch&iacute;n c&oacute; thể lọc được độc tố trong rượu v&agrave; lọc nước để uống. V&igrave; vậy nếu nước tưới của bạn kh&ocirc;ng đảm bảo, nước ứ đọng l&acirc;u th&igrave; trấu hun cũng sẽ gi&uacute;p lọc sạch để c&acirc;y kh&ocirc;ng bị nhiễm vi khuẩn từ nước</p>\r\n\r\n<h2><strong>C&Aacute;CH SỬ DỤNG TRẤU HUN LẪN NGUY&Ecirc;N C&Aacute;NH</strong></h2>\r\n\r\n<p>V&igrave; trấu hun lẫn kh&ocirc;ng thể cung cấp đủ tất cả c&aacute;c chất dinh dưỡng cho c&acirc;y n&ecirc;n bạn c&oacute; thể trộn th&ecirc;m c&aacute;ch ph&acirc;n b&oacute;n kh&aacute;c.</p>\r\n\r\n<p>C&aacute;ch sử dụng đơn giản nhất l&agrave; 5 đất, 3 trấu, 2 ph&acirc;n th&igrave; ph&acirc;n ở đ&acirc;y bạn c&oacute; thể linh hoạt ph&ugrave; hợp với nơi m&igrave;nh sinh sống, c&oacute; thể l&agrave; ph&acirc;n tr&ugrave;n quế, ph&acirc;n b&ograve; hoặc c&aacute;c ph&acirc;n hữu cơ kh&aacute;c.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"top: 620px; left: 471.75px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n'),
(103, 'Cây xoài', 599000, 23, 'Cây có nhiều quy cách và đường kính để phù hợp với công trình của bạn, đường kính phổ biến là từ 15cm đến 30cm đo cách gốc 1m. Cây có hàng đánh thẳng lẫn giâm ủ. Quý khách vui lòng liên hệ được giá chính xác.', '<h2><strong>ĐẶC ĐIỂM CỦA C&Acirc;Y XO&Agrave;I</strong></h2>\r\n\r\n<p><strong>T&ecirc;n thường gọi</strong>: C&acirc;y xo&agrave;i, c&acirc;y xo&agrave;i ta, xo&agrave;i &uacute;c, xo&agrave;i tượng, xo&agrave;i th&aacute;i, xo&agrave;i c&aacute;t&hellip;</p>\r\n\r\n<p><strong>T&ecirc;n tiếng anh</strong>: Mango.</p>\r\n\r\n<p><strong>T&ecirc;n khoa học</strong>: Mangifera indica L.</p>\r\n\r\n<p><strong>Họ thực vật</strong>: chi Xo&agrave;i thuộc họ &ETH;&agrave;o lộn hột (Anacardiaceae) l&agrave; một loại c&acirc;y ăn quả nhiệt đới.</p>\r\n\r\n<p><strong>Nguồn gốc xuất xứ</strong>: C&acirc;y giống c&oacute; nguồn gốc đến từ c&aacute;c nước c&oacute; kh&iacute; hậu nhiệt đới ở Ch&acirc;u &Aacute;.</p>\r\n\r\n<p><strong>Ph&acirc;n bố</strong>: Năm 1997, trung t&acirc;m c&acirc;y ăn quả Long Định tuyển chọn ở Việt Nam du nhập một giống xo&agrave;i nguồn gốc từ Ấn Độ c&oacute; t&ecirc;n gọi l&agrave; Xo&agrave;i C&aacute;t H&ograve;a Lộc v&agrave; được nh&acirc;n giống tại tỉnh Tiền Giang.</p>\r\n\r\n<p>Hiện nay, c&acirc;y được trồng ở khắp Việt Nam v&agrave; nhiều giống kh&aacute;c nhau.</p>\r\n\r\n<p><strong>Th&acirc;n v&agrave; c&agrave;nh</strong>: C&acirc;y thuộc loại c&acirc;y gỗ lớn, sống l&acirc;u năm. C&oacute; chiều cao trung b&igrave;nh từ 10 &ndash; 30m. Tuy nhi&ecirc;n, t&ugrave;y v&agrave;o độ tuổi v&agrave; giống c&acirc;y kh&aacute;c nhau m&agrave; độ lớn của c&agrave;nh v&agrave; t&aacute;n l&aacute; sẽ c&oacute; sự ch&ecirc;nh lệch.</p>\r\n\r\n<p>Th&acirc;n của c&acirc;y c&oacute; m&agrave;u n&acirc;u sậm hoặc trắng khi gi&agrave;.</p>\r\n\r\n<p><strong>Rễ</strong>: C&acirc;y c&oacute; rễ cọc c&oacute; thể ăn s&acirc;u xuống đất từ 5 &ndash; 8m, gi&uacute;p c&acirc;y c&oacute; thể chịu hạn v&agrave; đứng vững tr&atilde;i. Rễ tập trung quanh gốc ở phạm vi 2m.</p>\r\n\r\n<p><strong>L&aacute;</strong>: H&igrave;nh d&aacute;ng thon d&agrave;i, nhỏ v&agrave; mảnh mai, c&acirc;y thuộc dạng l&aacute; đơn. C&aacute;c c&aacute;nh l&aacute; mọc so le, xen kẽ qua c&aacute;c c&agrave;nh c&acirc;y, c&oacute; chiều d&agrave;i khoảng từ 15 &ndash; 30cm.</p>\r\n\r\n<p>Một trong những ưu điểm của c&acirc;y l&agrave; rất &iacute;t rụng l&aacute;. Ch&iacute;nh v&igrave; thế m&agrave; c&acirc;y được chọn l&agrave;m&nbsp;<a href=\"https://webcaycanh.com/cay-cong-trinh/\">c&acirc;y c&ocirc;ng tr&igrave;nh</a>.</p>\r\n\r\n<p>L&aacute; c&acirc;y khi non sẽ c&oacute; m&agrave;u hồng cam sau đ&oacute; l&agrave; ngả sang đỏ sẫm, mặt l&aacute; b&oacute;ng mượt v&agrave; c&oacute; m&agrave;u xanh đậm khi trưởng th&agrave;nh. T&ugrave;y theo điều kiện m&ocirc;i trường sống m&agrave; một c&acirc;y c&oacute; thể cho ra 3 &ndash; 4 đợt chồi trong 1 năm.</p>\r\n\r\n<p><strong>Hoa</strong>: Mọc th&agrave;nh ch&ugrave;m k&eacute;p ở ngọn c&agrave;nh, ch&ugrave;m d&agrave;i khoảng 30cm, c&oacute; 200 &ndash; 400 hoa tr&ecirc;n một ch&ugrave;m, c&oacute; m&agrave;u v&agrave;ng v&agrave; nhỏ, gồm 5 đ&agrave;i nhỏ v&agrave; một lớp l&ocirc;ng trắng ph&iacute;a ngo&agrave;i, cả 5 c&aacute;nh hoa đều c&oacute; tuyến mật, chỉ c&oacute; 2 nhị trong số 5 nhị sẽ sinh sản được.</p>\r\n\r\n<p>C&aacute;c ch&ugrave;m hoa c&oacute; m&agrave;u v&agrave;ng, m&ugrave;i thơm nhẹ. B&igrave;nh thường c&acirc;y sẽ trổ hoa v&agrave;o 2 vụ trong năm đ&oacute; l&agrave;: m&ugrave;a đ&ocirc;ng v&agrave; m&ugrave;a h&egrave;.</p>\r\n\r\n<p>Mỗi một ch&ugrave;m hoa xo&agrave;i sẽ c&oacute; 2 loại: lưỡng t&iacute;nh v&agrave; hoa đực. Hoa lưỡng t&iacute;nh sẽ chiếm khoảng 1 &ndash; 36%. C&acirc;y thuộc loại giống thụ phấn ch&eacute;o, phụ thuộc v&agrave;o c&ocirc;n tr&ugrave;ng đưa phấn l&agrave; chủ yếu. Thời điểm thụ phấn tốt nhất l&agrave; l&uacute;c mặt trời mọc, nhụy đực tung phấn v&agrave;o khoảng từ 8 &ndash; 10 giờ s&aacute;ng.</p>\r\n\r\n<p><strong>Quả</strong>: C&oacute; m&agrave;u xanh mướt b&ecirc;n ngo&agrave;i vỏ khi c&ograve;n non, chuyển dần sang v&agrave;ng đều khi ch&iacute;n, thịt quả v&agrave;ng, thơm, b&ecirc;n trong nh&acirc;n c&oacute; xơ. Về độ ngọt th&igrave; t&ugrave;y thuộc v&agrave;o giống c&acirc;y trồng về k&iacute;ch thước, h&igrave;nh dạng, m&agrave;u sắc sẽ cho độ ngọt kh&aacute;c nhau. H&igrave;nh d&aacute;ng của quả l&agrave; tr&ograve;n hoặc h&igrave;nh bầu dục c&oacute; chiều d&agrave;i khoảng 5 &ndash; 25cm.</p>\r\n\r\n<p><strong>Hạt</strong>: Cứng v&agrave; k&iacute;ch thước to, nhỏ t&ugrave;y giống c&acirc;y. Vỏ b&ecirc;n ngo&agrave;i quả khi ch&iacute;n sẽ mềm như da, mịn v&agrave; b&oacute;ng lo&aacute;ng, c&oacute; một hương đặc trưng, ngọt dịu. Đặc biệt, những quả trưởng th&agrave;nh cho ra hạt sẽ dễ nảy mầm v&agrave; trồng tốt hơn.</p>\r\n\r\n<p><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></p>\r\n');
INSERT INTO `sanpham` (`masanpham`, `tensanpham`, `gia`, `iddm`, `mota`, `motachitiet`) VALUES
(104, 'Cây hạnh phúc dáng tree to', 899000, 23, 'Cây Có chiều cao cả chậu là 1m8, tán rộng khoảng 65cm. Phù hợp để trang trí nội thất cho các không gian lớn, như đại sảnh, văn phòng chung, phòng khách, phòng sếp, quà tặng khai trương, tân gia....', '<h2>ĐẶC ĐIỂM CỦA C&Acirc;Y HẠNH PH&Uacute;C D&Aacute;NG TREE TO</h2>\r\n\r\n<p><strong>T&ecirc;n thường gọi</strong>:&nbsp;<a href=\"https://webcaycanh.com/cay-hanh-phuc-dang-tree-to/\">C&acirc;y Hạnh Ph&uacute;c d&aacute;ng tree to</a>, c&acirc;y Hạnh Ph&uacute;c đơn th&acirc;n to, c&acirc;y Hạnh Ph&uacute;c tho&aacute;t th&acirc;n</p>\r\n\r\n<p><strong>T&ecirc;n khoa học</strong>: Radermachera sinica</p>\r\n\r\n<p><strong>Chi (genus)</strong>: Heteropanax</p>\r\n\r\n<p><strong>Lo&agrave;i (species)</strong>: R. sinica</p>\r\n\r\n<p><strong>Nguồn gốc</strong>: Rừng nhiệt đới Trung Quốc v&agrave; Đ&ocirc;ng Nam &Aacute;</p>\r\n\r\n<p>C&acirc;y thuộc th&acirc;n gỗ trong tự nhi&ecirc;n n&oacute; c&oacute; thể cao đến 30m v&agrave; đường k&iacute;nh gốc l&ecirc;n tới 1m.</p>\r\n\r\n<p>L&aacute; c&acirc;y dạng l&ocirc;ng chim, 1 c&agrave;nh l&aacute; c&oacute; thể d&agrave;i từ 20 &ndash; 70cm, rộng từ 15 &ndash; 25cm, bề mặt l&aacute; d&agrave;i từ 2-4cm. C&acirc;y c&oacute; 3 l&aacute; cuối tạo th&agrave;nh h&igrave;nh tr&aacute;i tim, l&aacute; non c&oacute; m&agrave;u xanh non chuyển dần sang xanh đậm, c&oacute; lẽ ch&iacute;nh v&igrave; những điều n&agrave;y m&agrave; c&acirc;y c&oacute; t&ecirc;n l&agrave;&nbsp;<a href=\"https://webcaycanh.com/cay-hanh-phuc/\"><strong>c&acirc;y Hạnh Ph&uacute;c</strong></a>. Sau khi&nbsp;đơm hoa c&acirc;y cũng kết quả, quả giống h&igrave;nh quả&nbsp;đậu.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2019/02/la-cay-hanh-phuc.jpg\"><img alt=\"\" src=\"https://webcaycanh.com/wp-content/uploads/2019/02/la-cay-hanh-phuc-400x400.jpg\" style=\"height:400px; width:400px\" /></a></p>\r\n\r\n<p><em>Ba l&aacute; c&acirc;y Hạnh Ph&uacute;c gh&eacute;p lại th&agrave;nh h&igrave;nh tr&aacute;i tim&nbsp;</em></p>\r\n\r\n<p>C&acirc;y c&oacute; hoa trắng hoặc v&agrave;ng nhạt, hoa c&oacute; năm c&aacute;nh thường mọc th&agrave;nh ch&ugrave;m, dạng loa k&egrave;n, tuy nhi&ecirc;n c&acirc;y chỉ ra hoa khi điều kiện đủ nắng nếu bạn trồng b&ecirc;n ngo&agrave;i trời, hoặc &iacute;t nhất cũng phải c&oacute; nắng đến buổi trưa, c&ograve;n&nbsp;<a href=\"https://webcaycanh.com/cay-canh-trong-nha/\">c&acirc;y trồng trong nh&agrave;</a>&nbsp;th&igrave; rất kh&oacute; ra được hoa.</p>\r\n\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2019/02/hoa-cay-hanh-phuc.jpg\"><img alt=\"hoa cây hạnh phúc\" src=\"https://webcaycanh.com/wp-content/uploads/2019/02/hoa-cay-hanh-phuc-400x400.jpg\" style=\"height:400px; width:400px\" /></a></p>\r\n\r\n<p><em>Hoa c&acirc;y hạnh ph&uacute;c</em></p>\r\n\r\n<h2>&Yacute; NGHĨA V&Agrave; T&Aacute;C DỤNG CỦA C&Acirc;Y HẠNH PH&Uacute;C TO</h2>\r\n\r\n<h3><em>1. Phong thủy</em></h3>\r\n\r\n<p>C&aacute;i t&ecirc;n một phần n&agrave;o đ&atilde; n&oacute;i n&ecirc;n &yacute; nghĩa phong thủy của c&acirc;y. C&acirc;y mang đến cho gia chủ hạnh ph&uacute;c, gi&uacute;p c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh lu&ocirc;n lu&ocirc;n đo&agrave;n kết, h&ograve;a thuận, giữ g&igrave;n h&ograve;a kh&iacute;. Đối với c&ocirc;ng ty th&igrave; đồng nghiệp lu&ocirc;n niềm nở, vui vẻ gi&uacute;p đỡ v&agrave; hỗ trợ lẫn nhau.</p>\r\n\r\n<p>Khi tinh thần vui vẻ, gia đ&igrave;nh hạnh ph&uacute;c th&igrave; c&ocirc;ng việc sẽ thuận lợi hơn, l&agrave;m ăn may mắn gi&uacute;p sự nghiệp v&agrave; tiền bạc ph&aacute;t triển.</p>\r\n\r\n<h3><em><strong>2. T&aacute;c dụng</strong></em></h3>\r\n\r\n<ul>\r\n	<li><strong>Điều h&ograve;a kh&ocirc;ng kh&iacute;</strong>: Một trong những t&aacute;c dụng m&agrave; c&acirc;y xanh n&agrave;o cũng mang đến đ&oacute; l&agrave; điều h&ograve;a kh&ocirc;ng kh&iacute;, lọc bụi bẩn. Với t&aacute;n l&aacute; xanh v&agrave; rậm th&igrave; c&acirc;y Hạnh Ph&uacute;c l&agrave;m việc n&agrave;y rất tốt.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><strong>Trang tr&iacute; kh&ocirc;ng gian:&nbsp;</strong>Từ l&acirc;u kh&ocirc;ng gian xanh đ&atilde; được nhiều người ưa th&iacute;ch, v&igrave; m&agrave;u xanh của l&aacute; gi&uacute;p giảm stress, gi&uacute;p kh&ocirc;ng gian sống h&agrave;i h&ograve;a hơn.&nbsp;<strong>C&acirc;y Hạnh Ph&uacute;c</strong>&nbsp;lại c&oacute; nhiều k&iacute;ch cỡ kh&aacute;c nhau từ&nbsp;<a href=\"https://webcaycanh.com/cay-canh-de-ban/\">c&acirc;y nhỏ để b&agrave;n</a>, cho đến&nbsp;<a href=\"https://webcaycanh.com/cay-canh-loai-to/\">c&acirc;y lớn để s&agrave;n</a>, gi&uacute;p bạn dễ d&agrave;ng chọn được c&acirc;y ph&ugrave; hợp với kh&ocirc;ng gian.</li>\r\n</ul>\r\n');

